PREFIX foaf:<http://xmlns.com/foaf/0.1> PREFIX sib:<http://www.ins.cwi.nl/sib/vocabulary/> PREFIX sioc:<http://rdfs.org/sioc/ns#> SELECT ?post1 ?post2 ?tag FROM STREAM <poststream> [RANGE 15s STEP 1s]
WHERE {?post1 sib:hashtag ?tag. ?post2 sib:hashtag ?tag. ?user sioc:creator_of ?post1. ?user sioc:creator_of ?post2. filter (?post1 !=?post2)
}
